<?phpclass Html_Element_GroupSubmit extends Html_ContainerElement{    protected function prependsName()    {        return false;    }    public function toString()    {        $this->invalidAttribute('name');        $this->addClass('adm-edit-buttons');        $attrs = $this->getAttributesString();        $str = '<div ' . $attrs . '>';        $str .= parent::toString();        $str .= '</div>';        return $str;    }}