<?phpclass Html_Element_Group extends Html_ContainerElement{    const DEFAULT_CSS_CLASS = 'bz-form-group';    public function initAttributes()    {        parent::initAttributes();        $this->template('elements/group');        $this->validAttribute('name', 'string', false);        $this->addClass(self::DEFAULT_CSS_CLASS);    }    protected function prependsName()    {        return true;    }}