<?phpclass Html_jQuery_Tab extends Html_ContainerElement{    public function initAttributes()    {        parent::initAttributes();        $this->validAttribute('title',  'string', false);        $this->validAttribute('name',   'string', false);        $this->validAttribute('active', 'boolean', false);        $this->addClass('tab-pane');        $this->template('elements/jquery/tab');    }    public function toString()    {        if ($this->active()) {            $this->addClass('active');        }        return parent::toString();    }}