<?phpclass Html_jQuery_Tabs extends Html_ContainerElement{    public function initAttributes()    {        parent::initAttributes();        $this->validAttribute('name',  'string', false);        $this->template('elements/jquery/tabs');        $this->addClass('nav nav-tabs');        // print_r($this->form->id());    }    protected function prependsName()    {        return false;    }    public function addTab($title, $id = null)    {        if (empty($id)) {            $id = 'tab' . (count($this->elements) + 1);        }        $tab = new Html_jQuery_Tab($id);        if (count($this->elements) == 0) {            $tab->active(true);        }        $tab->title($title);        $this->addElement($tab);        return $tab;    }    public function toString()    {        Html_Form::addOnReady('$("#' . $this->id() . ' a").click(function (e) {            e.preventDefault();            $(this).tab("show");        });');        return parent::toString();    }}